import { useState, useCallback, useMemo } from "react";
import "bulma/css/bulma.min.css";
import {
  Container,
  Heading,
  Content,
  Button,
  Image,
} from "react-bulma-components";
import Hero from "react-bulma-components/esm/components/hero";
import Footer from "react-bulma-components/cjs/components/footer";
import logo from "./metamask.svg";
//import detectEthereumProvider from '@metamask/detect-provider'
import Web3 from "web3";
import Personal from "web3-eth-personal";

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [ethAddress, setEthAddress] = useState("");
  const [ethereum, setEthereum] = useState("");

  const hasMetamask = () => {
    if (typeof window.ethereum !== "undefined" && window.ethereum.isMetaMask) {
      console.log("MetaMask is installed!");
      setEthereum(window.ethereum);
      return true;
    } else {
      return false;
    }
  };

  const handlerClik = useCallback((e) => {
    e.preventDefault();
    if (hasMetamask()) {
      console.log("OK METAMASK");
    } else {
      alert("INSTALL METAMASK EXTENSIONS");
    }
    //setIsLoading(true);
    let web3 = new Web3(Web3.givenProvider);
    console.log(web3.eth.currentProvider);
    web3.eth.personal.getAccounts()
.then(console.log);
var personal = new Personal(Personal.givenProvider || 'ws://some.local-or-remote.node:8546');
console.log(web3.eth.accounts[0]);
   // web3.eth.personal.sign("Hello world", "0xf3141cC63bFCd477a2925eE2244C255ABEc4B414".toLowerCase(), "test password!")
  }, []);

  return (
    <div>
      <Hero size="fullheight">
        <Hero.Header backgroundColor="primary">
          <Container
            my={5}
            display="flex"
            flexDirection="column"
            alignItems="center"
            justifyContent="center"
          >
            <Image src={logo} size={128} />
            <Heading textColor={"white"}>Decentralized Login</Heading>
            <Heading textColor="grey" subtitle size={3}>
              Simple Login demo with MetaMask
            </Heading>
          </Container>
        </Hero.Header>
        <Hero.Body>
          <Container display="flex" justifyContent="center" alignItems="center">
            <Content>
              <Button
                size="medium"
                loading={isLoading}
                rounded="true"
                fullwidth="true"
                color="warning"
                onClick={handlerClik}
              >
                Login with MetaMask
              </Button>
            </Content>
          </Container>
        </Hero.Body>
        <Hero.Footer>
          <Footer>
            <Container>
              <Content style={{ textAlign: "center" }}>
                <p>
                  <strong>Decentralized Login Demo</strong> by{" "}
                  <a
                    href="https://www.francescoprimerano.it"
                    rel="noreferrer"
                    target="_blank"
                  >
                    Francesco Primerano
                  </a>
                  . The source code is licensed under
                  <a href="http://opensource.org/licenses/mit-license.php">
                    {" "}
                    MIT
                  </a>
                </p>
                <p>
                  The repository of this project is avaiable on{" "}
                  <a
                    href="https://github.com/pf55351/eva"
                    rel="noreferrer"
                    target="_blank"
                  >
                    github
                  </a>
                </p>
              </Content>
            </Container>
          </Footer>
        </Hero.Footer>
      </Hero>
    </div>
  );
}

export default App;
