import React,{useState} from "react";
import SweetAlert from "react-bootstrap-sweetalert";
import "bulma/css/bulma.min.css";
import { Content, Button } from "react-bulma-components";

function Modal({fieldModal}) {

    const {show}=fieldModal;
    const [showModal, setShowModal]=useState(show);

  return (
    <div>
      <SweetAlert
        show={showModal}
        success={false}
        danger={fieldModal.danger}
        title={fieldModal.title}
        showConfirm={false}
        onConfirm={console.log("A")}
      >
        <Content>
          <Button fullwidth={true} color="primary">
            OK
          </Button>
        </Content>
      </SweetAlert>
    </div>
  );
}

export default Modal;
